                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      package executerdemo;

public class StringReverserecur {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        String str = "Geeks for Geeks";
        StringReverserecur obj = new StringReverserecur();
       System.out.println( obj.reverseString(str));

	}
   static  String reverse(String str)
    {
        if ((str==null)||(str.length() <= 1))
        {
         //  System.out.println(str);
        	return str;
        }
        else
        {
            System.out.print(str.charAt(str.length()-1));
          return  reverse(str.substring(0,str.length()-1));
            
        }
    }
   public static String reverseString(String str)  
   {  
   //checks if the string is empty   
if ((str==null)||(str.length() <= 1))
   {  
   System.out.println("String is empty.")  ;
   //if the above condition is true returns the same string      
   return str;  
   }   
   else   
   {  
   return reverseString(str.substring(1))+str.charAt(0);  
   }  
   }  

}
